    (filled(normData.phone_e164) ? String(normData.phone_e164) : null)         // 1) Normalizar datos
 || toE164(uni.number)                                                         // 2) Unificador.number (sólo dígitos)
 || toE164(existeCli.Telefono ?? existeCli.telefono)                           // 3) Telefono desde MySQL si existe
 || fromRemoteJid(normData.ctx?.remoteJid)                                     // 4) remoteJid en Normalizar datos
 || fromRemoteJid(uni.remoteJid)                                              // 5) remoteJid en Unificador
 || fromRemoteJid(normChat.remoteJid)                                         // 6) remoteJid en Normalizar chat
 || null;

