  }
}];

const faltantes = ($json.faltantes || []).map(String);
const lista = faltantes.length ? faltantes.join(', ') : '(ninguno)';

const texto = 
  `No encontré estos códigos: ${lista}. ` +
  `Revisá que estén bien escritos o pedime el catálogo si necesitás buscar por nombre. ` +
  `No generé el carrito; decime los códigos válidos con cantidades (ej.: A001 x2).`;

return [{ json: { texto } }];

const v = $json || {};
return [{
  json: {
    has_open_cart: !!v.found && ['OPEN','CONFIRMING'].includes(v.status),
    cart_line_count: Number(v.lines || 0),
    summary_shown:
      v.status === 'CONFIRMING' &&
      v.version === v.confirm_version &&
      Number(v.lines || 0) > 0
  }
}];

// INPUT: items[0].json = { ops, catalogo }
// - ops: [{ op: "add"|"set"|"remove"|"clear", codigo|sku, cantidad, precio?, descripcion? }, ...]
// - catalogo: [{ codigo, precio, descripcion }, ...]
const input = items[0].json || {};
const ops = Array.isArray(input.ops) ? input.ops : [];
const catalogo = Array.isArray(input.catalogo) ? input.catalogo : [];

// Mapa de catálogo por código normalizado
const idx = new Map();
for (const row of catalogo) {
  const code = (row.codigo ?? row.sku ?? '').toString().trim().toUpperCase();
  if (!code) continue;
  idx.set(code, {
    precio: Number(row.precio ?? 0) || 0,
    descripcion: (row.descripcion ?? '').toString()
  });
}

const errores = [];
for (const op of ops) {
  // Normalizo campo de código
  const code = (op.codigo ?? op.sku ?? '').toString().trim().toUpperCase();
  op.codigo = code; // unifico en "codigo" (si tu SQL espera "o.codigo")
  delete op.sku;

  // Solo enriquezco add/set
  if (op.op === 'add' || op.op === 'set') {
    // Mantener si ya venía seteado, si no, tomar del índice
    const cat = idx.get(code);

    if (op.precio == null && cat) op.precio = cat.precio;
    if (!op.descripcion && cat) op.descripcion = cat.descripcion;

    // Limpieza de tipos
    op.precio = Number(op.precio ?? 0) || 0;
    if (typeof op.descripcion !== 'string') op.descripcion = (op.descripcion ?? '').toString();

    // Validaciones mínimas (para avisar arriba)
    if (!code) {
      errores.push('Operación sin código');
    } else if (!cat && (op.op === 'add' || op.op === 'set')) {
      errores.push(`${code}: SKU inexistente en catálogo`);
    } else if (op.precio <= 0) {
      errores.push(`${code}: sin precio (> 0)`);
    }

    // Cantidad: clamp opcional (1..99)
    if (op.op === 'set') {
      op.cantidad = Math.max(1, Math.min(99, Number(op.cantidad ?? 0) || 0));
    } else if (op.op === 'add') {
      op.cantidad = Math.max(1, Math.min(99, Number(op.cantidad ?? 1) || 1));
    }
  }
}

// Dejo todo listo para el batch SQL
return [{
  json: {
    ...input,
    ops,              // por compatibilidad
    ops_json: ops,    // si tu Postgres lee "ops_json"
    errores_pre: errores
  }
}];

// items: 0..N filas del MySQL
const catalogo = items.length ? items.map(i => i.json) : [];
return [{ json: { catalogo } }];

