// n8n Code node (Function)
// Normaliza cualquier variante del output del orquestador:
// {‚Ä¶}  |  {"output":{‚Ä¶}}  |  {"output":{"output":{‚Ä¶}}}  |  strings JSON (con o sin fences)

function stripFences(s){
  return String(s||'')
    .replace(/^\s*```[a-zA-Z0-9_-]*\s*/m,'')
    .replace(/\s*```\s*$/m,'')
    .trim();
}
function safeParse(x) {
  if (x == null) return null;
  if (typeof x === 'string') {
    const raw = stripFences(x);
    try { return JSON.parse(raw); } catch {}
    const a = raw.indexOf('{'), b = raw.lastIndexOf('}');
    if (a >= 0 && b > a) {
      try { return JSON.parse(raw.slice(a, b + 1)); } catch {}
    }
    return null;
  }
  return x;
}

function pickCandidate(obj) {
  // intenta todas las rutas conocidas
  const a = safeParse(obj);
  const b = safeParse(a?.output);
  const c = safeParse(a?.output?.output);
  return c ?? b ?? a ?? {};
}

function sanitize(json) {
  let { intencion, params, pregunta } = (json || {});

  // üöë incluir TODAS las intenciones v√°lidas
  const allowed = new Set([
    'crear_pedido',
    'editar_carrito',
    'listar_productos',
    'saludar_cliente',
    'pendiente_clarificacion',
    'cancelar_confirmacion',
  ]);

  if (!allowed.has(intencion)) {
    intencion = 'pendiente_clarificacion';
  }

  if (typeof params !== 'object' || params === null || Array.isArray(params)) {
    params = {};
  }

  // üìå Regla correcta de "pregunta":
  // - saludar_cliente y pendiente_clarificacion ‚Üí deben traer pregunta NO vac√≠a
  // - resto de intenciones ‚Üí pregunta = ""
  if (intencion === 'saludar_cliente' || intencion === 'pendiente_clarificacion') {
    if (typeof pregunta !== 'string' || !pregunta.trim()) {
      pregunta = '¬°Hola! ¬øPod√©s reformular tu solicitud con un poco m√°s de detalle?';
    } else {
      pregunta = String(pregunta);
    }
  } else {
    pregunta = '';
  }

  // Orden y shape final
  return { intencion, params, pregunta };
}

const out = [];
for (const item of items) {
  const raw = item.json;
  const maybeText = raw?.text ?? raw?.message ?? null;
  const base = safeParse(maybeText) ?? raw;        // si vino como string en text/message
  const candidate = pickCandidate(base);           // desanidar "output" y variantes
  const finalCandidate = safeParse(candidate) ?? candidate;
  out.push({ json: sanitize(finalCandidate) });
}
return out;

// n8n Code node (Function)
// Entrada ej.: { output: "{\"output\":{\"nombre\":null,...}}" }
// Salida: { nombre, apellido, direccion, completo, pregunta }

function parseOutput(val) {
  let r = val;
  for (let i = 0; i < 3; i++) {           // hasta triple anidaci√≥n
    if (typeof r === 'string') {
      try { r = JSON.parse(r); } catch { break; }
    }
    if (r && typeof r === 'object' && 'output' in r) {
      r = r.output;                        // desanidar "output"
      continue;
    }
    break;
  }
  return (r && typeof r === 'object') ? r : {};
}

const out = [];
for (const item of items) {
  const inner = parseOutput(item.json.output ?? item.json);
  out.push({
    json: {
      nombre: inner.nombre ?? null,
      apellido: inner.apellido ?? null,
      direccion: inner.direccion ?? null,
      completo: inner.completo ?? false,
      pregunta: (typeof inner.pregunta === 'string') ? inner.pregunta : ''
    }
  });
}
return out;

// n8n Code (Function) ‚Äî Mueve el contenido de `output` a la ra√≠z del JSON
// Soporta output como string JSON (con o sin ```json) u objeto; limpia y clampa confidence

function stripFences(s){
  return String(s||'')
    .replace(/^\s*```[a-zA-Z0-9_-]*\s*/m,'')
    .replace(/\s*```\s*$/m,'')
    .trim();
}
function tryParse(x){
  if (x == null) return null;
  if (typeof x === 'object') return x;
  const raw = stripFences(String(x));
  try { return JSON.parse(raw); } catch {}
  const a = raw.indexOf('{'), b = raw.lastIndexOf('}');
  if (a>=0 && b>a) {
    const sub = raw.slice(a,b+1);
    try { return JSON.parse(sub); } catch {}
    try {
      const fixed = sub.replace(/(['"])?([a-zA-Z0-9_]+)\1\s*:/g,'"$2":').replace(/'/g,'"');
      return JSON.parse(fixed);
    } catch {}
  }
  return null;
}
function clamp01(n){ const x = Number(n); return Number.isFinite(x) ? Math.min(1,Math.max(0,x)) : 0; }
function sanitize(obj){
  const ALLOWED = new Set(['CLOSE','CONFIRM','NONE']);
  let cmd = String(obj?.command ?? '').trim().toUpperCase();
  if (!ALLOWED.has(cmd)) cmd = 'NONE';
  let confidence = clamp01(obj?.confidence);
  return { command: cmd, confidence };
}

return $input.all().map(item => {
  const j = { ...item.json };
  let parsed = tryParse(j.output);
  if (parsed && parsed.output && !parsed.command) parsed = tryParse(parsed.output) || parsed;
  const { command, confidence } = sanitize(parsed || {});
  j.command = command;
  j.confidence = confidence;
  delete j.output;
  return { json: j, pairedItem: item.pairedItem };
});

// Formatear Pedido ‚Äî REEMPLAZO M√çNIMO
function normCode(c){
  return String(c||'')
    .normalize('NFKC')
    .toUpperCase()
    .replace(/[\s\-._]/g,'')
    .replace(/[^A-Z0-9]/g,'');
}
function isPosInt(n){ return Number.isInteger(n) && n > 0; }

const inx = items?.[0]?.json ?? {}; // viene del orquestador: { intencion, params:{ cliente, productos } }
const cliente = Number(inx.params?.cliente ?? NaN);
const canal = 'whatsapp';

// üëá fuente can√≥nica del tel√©fono (ya normalizado a E.164 con '+')
const phone_e164 = $('Normalizar Cliente').first().json.phone_e164;

const raw = Array.isArray(inx.params?.productos) ? inx.params.productos : [];
const productos_validos = [];
